import React, { useContext } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import LoginPage from "./pages/LoginPage";
import SignupPage from "./pages/SignupPage";
import ProductsPage from "./pages/ProductsPage";
import ProtectedRoute from "./components/ProtectedRoute";
import { AuthProvider, AuthContext } from "./context/AuthContext";

const App = () => {
    const { auth } = useContext(AuthContext); // Access authentication status from AuthContext

    return (
        <AuthProvider>
            <Router>
                <Routes>
                    {/* Default Route */}
                    <Route
                        path="/"
                        element={
                            auth.isAuthenticated ? <Navigate to="/products" /> : <Navigate to="/login" />
                        }
                    />
                    {/* Login Route */}
                    <Route
                        path="/login"
                        element={
                            auth.isAuthenticated ? <Navigate to="/products" /> : <LoginPage />
                        }
                    />
                    {/* Signup Route */}
                    <Route
                        path="/signup"
                        element={
                            auth.isAuthenticated ? <Navigate to="/products" /> : <SignupPage />
                        }
                    />
                    {/* Protected Products Route */}
                    <Route
                        path="/products"
                        element={
                            <ProtectedRoute>
                                <ProductsPage />
                            </ProtectedRoute>
                        }
                    />
                </Routes>
            </Router>
        </AuthProvider>
    );
};

export default App;
