import React, { useState } from 'react';

const SignupPage = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const [errorMessage, setErrorMessage] = useState(''); // Use errorMessage

    const handleSignup = (e) => {
        e.preventDefault();

        // Check if fields are empty or invalid
        if (!username || !email || !password) {
            setErrorMessage('All fields are required');
            return;
        }

        // Simulate signup process (replace with actual signup logic)
        console.log('Signing up with:', { username, password, email });
        setErrorMessage(''); // Clear error message on successful signup

        // Redirect after successful signup (optional)
    };

    return (
        <div>
            <h1>Sign Up</h1>
            <form onSubmit={handleSignup}>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                {errorMessage && <p>{errorMessage}</p>} {/* Display error message */}
                <button type="submit">Sign Up</button>
            </form>
        </div>
    );
};

export default SignupPage;
