import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useContext } from 'react';
import { AuthContext } from '../context/AuthContext'; // Import AuthContext

const LoginPage = () => {
    const { setAuth } = useContext(AuthContext); // Access setAuth from context
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = (e) => {
        e.preventDefault();

        // Simulate login, replace this with actual login logic
        const token = 'some-token'; // Simulate getting token after login

        // Set the auth state and store token in localStorage
        setAuth({ isAuthenticated: true, token });
        localStorage.setItem('accessToken', token);

        // Redirect to the products page after successful login
        navigate('/products');
    };

    return (
        <div>
            <h1>Login</h1>
            <form onSubmit={handleLogin}>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button type="submit">Login</button>
            </form>
        </div>
    );
};

export default LoginPage;
